(function () {
  document.addEventListener('DOMContentLoaded', () => {
    const {
        getValue, 
        setValue, 
        addEventListener, 
        querySelector
    } = window.dynamicFields.DynamicFieldsExamples_CkEditor_Value;

    const editorElement = querySelector('.editor');
    const ckEditor = CKEDITOR.replace(editorElement);
    ckEditor.on('change', function () {
      let content = ckEditor.getData();
      console.log("Editor changed");
      setValue({ content: content })
    });
    
    addEventListener('value', (object) => {
      if (object?.content) {
        ckEditor.setData(object?.content);
      }
    }, {init: true});
  });
})();
