(function () {
    const {
        getValue, 
        setValue, 
        addEventListener, 
        querySelector
    } = window.dynamicFields.DynamicFieldsExamples_ColorPicker_Value;
    
    const picker = querySelector('hex-color-picker');
    
    picker.addEventListener('color-changed', (event) => {
      const newColor = event.detail.value;
      const object = {
        color: newColor
      };
      setValue(object);
    });
    
    addEventListener('value', (object) => {
        const color = object?.color;
        picker.color = color;
        picker.setAttribute('color', color);
    }, {init: true});
})();
