(function () {
    const {
        setValue, 
        addEventListener, 
        querySelector,
        getParentId
    } = window.dynamicFields.DynamicFieldsExamples_ColorisJs_Value;

    const coloris = querySelector('input[data-coloris]');
    coloris.id = `${getParentId()}_coloris`;
    
    coloris.addEventListener('input', event => {
        if (event.target.value === "") {
            setValue({ color: null});
        } else {
            setValue({ color: event.target.value});
        }
    });

    addEventListener('value', (object) => {
        coloris.value = object?.color;
    }, {init: true});
})();
