(function () {
  document.addEventListener('DOMContentLoaded', () => {
    const {
        setValue, 
        addEventListener, 
        querySelector,
        notify
    } = window.dynamicFields.DynamicFieldsExamples_GrapesJsEditor_Value;

    const container = querySelector('.gjs');   
    const editor = grapesjs.init({
      container: container,
      height: '500px',
      width: '100%',
      fromElement: true,
      storageManager: false,
    });
    editor.on('change', () => {
      const html = editor.getHtml();
      const css = editor.getCss();
      const object = {
        html,
        css
      }
      setValue(object);
    });

    addEventListener('value', (object) => {
      const { html, css } = object ?? {};
      if (html && css) {
        editor.setComponents(html);
        editor.setStyle(css);
      }
    }, {init: true});
  });
})();
