(function () {
  document.addEventListener('DOMContentLoaded', () => {
    const {
        setValue, 
        addEventListener, 
        querySelector
    } = window.dynamicFields.DynamicFieldsExamples_JsonEditor_Value;
    
    const container = querySelector(".jsoneditor");
    const options = {
      mode: 'tree', // or 'code', 'form', etc.
      onChange: function () {
        try {
          const json = editor.get();
          setValue({ json : json});
        } catch (err) {
          console.error('Invalid JSON:', err);
        }
      }
    };
    const editor = new JSONEditor(container, options);

    const initialJson = {
      //"Array": [1, 2, 3],
      //"Boolean": true,
      //"Null": null,
      //"Number": 123,
      //"Object": {"a": "b", "c": "d"},
      //"String": "Hello World"
    };
    editor.set(initialJson);
    
    addEventListener('value', (object) => {
      if (object?.json) {
        editor.set(object?.json);
      }
    }, {init: true});
  });
})();
