(function () {
  document.addEventListener('DOMContentLoaded', () => {
    /* available functions */
    const {
        getValue, 
        setValue, 
        addEventListener, 
        querySelector, 
        closest,
        getPathBase,
        getLangDir
    } = window.dynamicFields.DynamicFieldsExamples_Jspreadsheet_Value;

    var data = [
        ['Jazz', 'Honda', '2019-02-12', '', true, '$ 2.000,00', '#777700'],
        ['Civic', 'Honda', '2018-07-11', '', true, '$ 4.000,01', '#007777'],
    ];

    var spreadsheet = jspreadsheet(querySelector('.spreadsheet'), {
      data:data,
      columns: [
          { type: 'text', title:'Car', width:120 },
          { type: 'dropdown', title:'Make', width:200, source:[ "Alfa Romeo", "Audi", "Bmw" ] },
          { type: 'calendar', title:'Available', width:200 },
          { type: 'image', title:'Photo', width:120 },
          { type: 'checkbox', title:'Stock', width:80 },
          { type: 'numeric', title:'Price', width:100, mask:'$ #.##,00', decimal:',' },
          { type: 'color', width:100, render:'square', }
      ],
      onchange: function(instance, cell, x, y, value) {
        console.log("value changed");
		setValue({ data : spreadsheet.getData()});	
      }
  });
    
    addEventListener('value', (object) => {
      if (object?.data) {
        spreadsheet.setData(object?.data);
      }
    }, {init: true});
  });
})();
