const { useState, useEffect } = React;
const { Button, TextField, Container, Typography, Box } = MaterialUI;

(function () {
    const dynamicField = window.dynamicFields.DynamicFieldsExamples_ReactComponent_Value;

    function App() {
      const [name, setName] = useState("");
      const [greeting, setGreeting] = useState("");

      useEffect(() => {
        function handleValueChange(object) {
          setName(object?.name ?? "Stranger");
        }
        dynamicField.addEventListener("value", handleValueChange, {init: true});
        return () => {
          dynamicField.removeEventListener("value", handleValueChange);
        };
      }, []); 

      const handleClick = () => {
        setGreeting(`Hello, ${name || "Stranger"}! ðŸ‘‹`);
        dynamicField.setValue({ name : name });
      };

      const handleChange = (e) => {
        setName(e.target.value);
        dynamicField.setValue({ name : e.target.value });
      };

        return (
          <Container maxWidth="sm" sx={{ mt: 5, textAlign: "center" }}>
            <Typography variant="h4" gutterBottom>
              Simple React + MUI Example
            </Typography>
            <Box sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
              <TextField
                label="Enter your name"
                variant="outlined"
                value={name}
                onChange={handleChange}
              />
              <Button variant="contained" onClick={handleClick}>
                Greet Me
              </Button>
              {greeting && (
                <Typography variant="h6" color="primary">
                  {greeting}
                </Typography>
              )}
            </Box>
          </Container>
        );
      }

    const root = ReactDOM.createRoot(dynamicField.querySelector(".react-root"));
    root.render(<App />);

    addEventListener('value', (object) => {
      /* your code */
    }, {init: true});
})();
