(function () {
    const {
        getValue, 
        setValue, 
        addEventListener, 
        querySelector
    } = window.dynamicFields.DynamicFieldsExamples_Leafletjs_Value;
    
    const mapContainer = querySelector('.map');
    const map = L.map(mapContainer).setView([51.505, -0.09], 13);
    const tiles = L.tileLayer('https://tile.openstreetmap.org/{z}/{x}/{y}.png', {
        maxZoom: 19,
