(function () {
  document.addEventListener('DOMContentLoaded', () => {
    /* available functions */
    const {
        getValue, 
        setValue, 
        addEventListener, 
        querySelector
    } = window.dynamicFields.DynamicFieldsExamples_FullCalendar_Value;
    
    var Calendar = FullCalendar.Calendar;
    var Draggable = FullCalendar.Draggable;

    var containerEl = querySelector('.external-events');
    var calendarEl = querySelector('.calendar');
    var checkbox = querySelector('.drop-remove');

    // initialize the external events
    // -----------------------------------------------------------------

    new Draggable(containerEl, {
      itemSelector: '.fc-event',
      eventData: function(eventEl) {
        return {
          title: eventEl.innerText
        };
      }
    });

    // initialize the calendar
    // -----------------------------------------------------------------

    var internalChange = false;
    
    var calendar = new Calendar(calendarEl, {
      headerToolbar: {
        left: 'prev,next today',
        center: 'title',
        right: 'dayGridMonth,timeGridWeek,timeGridDay'
      },
      editable: true,
      droppable: true, // this allows things to be dropped onto the calendar
      drop: (event) => {
        // is the "remove after drop" checkbox checked?
        if (checkbox.checked) {
          // if so, remove the element from the "Draggable Events" list
          event.draggedEl.parentNode.removeChild(event.draggedEl);
        }
        setTimeout(() => {
          console.log('Event droped');
		  setValue({events : calendar.getEvents()});
        }, 0);
      },
      eventAdd: (info) => {
        if (!internalChange) {
          console.log('Event added');
          setValue({events : calendar.getEvents()});
        }
      },
      eventChange: (info) => {
        console.log('Event changed');
        setValue({events : calendar.getEvents()});
      },
      eventRemove: (info) => {
        console.log('Event removed');
        setValue({events : calendar.getEvents()});
      }
    });

    addEventListener('value', (object) => {
      calendar.removeAllEvents();
	  internalChange = true;
      object?.events?.forEach(ev => calendar.addEvent(ev));
      internalChange = false;
      calendar.render();
    }, {init: true});
  });
})();

