(function () {
  document.addEventListener('DOMContentLoaded', () => {
    const {
      getValue, 
      setValue, 
      addEventListener, 
      querySelectorAll, 
    } = window.dynamicFields.DynamicFieldsExamples_BootstrapFields_Value;
    
    const getDict = (allFields) => {
      return Array.from(allFields).reduce((obj, el) => {
        let value;
        if (el.type === 'checkbox') {
          value = el.checked;
        } else if (el.type === 'radio') {
          if (el.checked) {
            value = el.value;
          } else {
            if (el.name in obj) return obj;
            value = null;
          }
        } else if (el.tagName === 'SELECT' && el.multiple) {
          value = Array.from(el.selectedOptions).map(opt => opt.value);
        } else {
          value = el.value;
        }
        obj[el.name] = value;
        return obj;
      }, {});
    };
    
    const setDict = (allFields, dict) => {
      Array.from(allFields).forEach(el => {
        const value = dict?.[el.name];
        if (value === undefined) return;
        if (el.type === 'checkbox') {
          if (Array.isArray(value)) {
            el.checked = value.includes(el.value);
          } else {
            el.checked = Boolean(value);
          }
        } else if (el.type === 'radio') {
          el.checked = el.value === value;
        } else if (el.tagName === 'SELECT' && el.multiple) {
          Array.from(el.options).forEach(opt => {
            opt.selected = value.includes(opt.value);
          });
        } else {
          el.value = value;
        }
      });
    }
       
    const allFields = querySelectorAll('.fields-container input, .fields-container select, .fields-container textarea');

    allFields.forEach(element => {
      element.addEventListener('input', (event) => {
        if (event.target.matches('input, textarea')) {
          console.log('Input changed', event.target.value);
          setValue(getDict(allFields));
        }
      });
      element.addEventListener('change', (event) => {
        const type = event.target.type;
        if (type === 'checkbox' || type === 'radio' || type === 'file' || type === 'range' || event.target.tagName.toLowerCase() === 'select') {
          console.log('Changed field:', event.target.tagName, type, event.target.value);
          setValue(getDict(allFields));
        }
      });
    });

    addEventListener('value', (object) => {
      setDict(allFields, object ?? {});
    }, {init: true});
  });
})();

const { useState, useEffect } = React;
const { Button, TextField, Container, Typography, Box } = MaterialUI;

(function () {
    const dynamicField = window.dynamicFields.DynamicFieldsExamples_ReactComponent_Value;

    function App() {
      const [name, setName] = useState("");
      const [greeting, setGreeting] = useState("");

      useEffect(() => {
        function handleValueChange(object) {
          setName(object?.name ?? "Stranger");
        }
        dynamicField.addEventListener("value", handleValueChange, {init: true});
        return () => {
          dynamicField.removeEventListener("value", handleValueChange);
        };
      }, []); 

      const handleClick = () => {
        setGreeting(`Hello, ${name || "Stranger"}! 👋`);
        dynamicField.setValue({ name : name });
      };

      const handleChange = (e) => {
        setName(e.target.value);
        dynamicField.setValue({ name : e.target.value });
      };

        return (
          <Container maxWidth="sm" sx={{ mt: 5, textAlign: "center" }}>
            <Typography variant="h4" gutterBottom>
              Simple React + MUI Example
            </Typography>
            <Box sx={{ display: "flex", flexDirection: "column", gap: 2 }}>
              <TextField
                label="Enter your name"
                variant="outlined"
                value={name}
                onChange={handleChange}
              />
              <Button variant="contained" onClick={handleClick}>
                Greet Me
              </Button>
              {greeting && (
                <Typography variant="h6" color="primary">
                  {greeting}
                </Typography>
              )}
            </Box>
          </Container>
        );
      }

    const root = ReactDOM.createRoot(dynamicField.querySelector(".react-root"));
    root.render(<App />);

    addEventListener('value', (object) => {
      /* your code */
    }, {init: true});
})();

